    let names: [String] = ["Куликов Илья", "Федотова Анна", "Юдина Мария", "Вишневский Владимир", "Архипова Мария"]
    
    //MARK: Functions
    public func generatePost() -> Post {
        var post = Post.init(content: nil, contentImage: nil, authorImage: avatarImages.randomElement()!, authorName: names.randomElement()!, likesCount: Int.random(in: 2..<69), date: Date())
        let isHaveImage = Bool.random()
        if isHaveImage {
            post.contentImage = postImages.randomElement()
        }
        let isHaveText = Bool.random()
        if isHaveText {
            post.content = postTexts.randomElement()
        }
        if isHaveText == false && isHaveImage == false {
            post.content = postTexts.randomElement()
        }
        return post
    }
        
    public func generateArrayOfPosts(count: Int) -> [Post] {
        var posts: [Post] = []
        for _ in 0..<count {
            posts.append(generatePost())
        }
        return posts
    }
    
    public func generateUser() -> User {
        return User(avatarImage: avatarImages.randomElement()!, name: names.randomElement()!, city: "Kazan", phoneNumber: "+89083902021", friendsCount: Int.random(in: 14..<200), subscribersCount: Int.random(in: 14..<200))
    }
